CREATE TABLE `events` (
  `id` int(2) NOT NULL,
  `name` varchar(29) DEFAULT NULL,
  `date` date DEFAULT NULL,
  `time` time(6) DEFAULT NULL,
  `location` varchar(22) DEFAULT NULL,
  `admission_fee` decimal(2,0) DEFAULT NULL,
  `type` varchar(11) DEFAULT NULL,
  `description` varchar(397) DEFAULT NULL,
  `imageURL` varchar(256) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

--
-- Dumping data for table `events`
--

INSERT INTO `events` (`id`, `name`, `date`, `time`, `location`, `admission_fee`, `type`, `description`, `imageURL`) VALUES
(1, 'Owls by Moonlight', '2024-01-15', '19:00:00.000000', 'Moonlit Grove', 12, 'Family', 'Join us for a magical stroll under the starry sky at Jem Wildlife Centre. Witness the mesmerizing flight of our rescued owls in their natural nocturnal habitat. It\'s a rare opportunity to learn about these mysterious creatures while they soar silently above you. This event is designed for families and photographers who are eager to capture the beauty of nature in the ethereal light of the moon.', 'owls_by_midnight_1.jpg'),
(2, 'Badger Watch', '2024-02-15', '17:00:00.000000', 'Badger\'s Den', 8, 'Family', 'As the day fades, embark on an educational journey into the life of Britain\'s native badgers. Our expert guides will share fascinating insights into the conservation of these shy creatures. Then, as evening draws in, observe the badgers as they forage and play—an intimate wildlife experience not to be missed. Open to guests of all ages, this event promotes wildlife awareness and preservation.', 'badger_watch_1.jpg'),
(3, 'Wildlife Photography Workshop', '2024-04-05', '10:00:00.000000', 'Lakeside Trail', 20, 'Adults Only', 'Delve into the art of capturing nature with our Wildlife Photography Workshop. Tailored for adult enthusiasts, this event offers expert guidance on technique and composition, ensuring you take home not just photos but skills. Set along our scenic Lakeside Trail, it\'s a chance to photograph a variety of wildlife in their habitats, with a focus on ethics and respect for our animal subjects.', 'wildlife_photography_workshop_1.jpg'),
(4, 'Hedgehog Haven', '2024-05-09', '13:00:00.000000', 'Hedgerow Hideout', 6, 'Children', 'Children will delight in our Hedgehog Haven event, where they\'ll discover the secret world of these charming creatures. Through interactive games and hands-on activities like creating hedgehog-friendly spaces, youngsters will learn about the importance of wildlife care and conservation in a fun, engaging environment.', 'hedgehog_haven_1.jpg'),
(5, 'Falconry Day', '2024-07-17', '11:00:00.000000', 'The Falcon Field', 15, 'Family', 'Experience the noble tradition of falconry with our interactive Falconry Day. Suitable for all ages, this family-friendly event allows you to get up close with birds of prey and learn about their role in the ecosystem. Witness breathtaking flying displays, participate in educational talks, and even handle the birds under expert supervision. An unforgettable day for the whole family. ', 'falconry_day_1.jpg'),
(6, 'Pond Dipping Adventure', '2024-07-16', '14:00:00.000000', 'Willow Pond', 6, 'Children', 'Grab your nets and join us for a Pond Dipping Adventure! Perfect for young explorers, this child-focused event dives into the fascinating world of pond ecosystems. Kids will love discovering and identifying creatures with our wildlife specialists, learning about aquatic habitats, and the importance of conservation. It\'s a wet and wild way to get hands-on with nature.', 'pond_dipping_adventure_1.jpg'),
(7, 'Deer Dusk Walk', '2024-08-14', '20:00:00.897000', 'Forest Glade', 10, 'Adults Only', 'Immerse yourself in the tranquility of the evening with our Deer Dusk Walk. Designed for adults, this peaceful stroll through our forest glade at twilight offers a chance to observe the elegant deer in their natural setting. A guide will share insights into deer behavior and conservation, creating a serene and educational experience amidst the beauty of nature.', 'deer_dusk_walk_1.jpg'),
(8, 'Wildlife Rescue Workshop', '2024-09-18', '09:00:00.000000', 'The Learning Lodge', 18, 'Adults Only', 'Our Wildlife Rescue Workshop is an intensive, adult-only event for those passionate about animal care. Learn first aid and rehabilitation techniques from our wildlife experts, practice with realistic scenarios, and gain invaluable knowledge that could help save a creature\'s life. It\'s a hands-on approach to learning how we can all help preserve our native wildlife.', 'wildlife_rescue_workshop_1.jpg'),
(9, 'Nature\'s Nighttime Symphony', '2024-10-31', '18:30:00.000000', 'The Listening Clearing', 7, 'Family', 'Spend Halloween night with us and be spellbound by the eerie and beautiful sounds of our nocturnal wildlife. This family-friendly event combines the natural ambiance of the night with storytelling, making it a perfect blend of education and entertainment. Discover the creatures that sing, hoot, and howl in the night, and let their music enchant you and your family.', 'natures_nighttime_symphony.jpg'),
(10, 'Winter Bird Feeder Crafting', '2024-12-11', '14:00:00.000000', 'The Craft Barn', 4, 'Children', 'Get crafty with our Winter Bird Feeder Crafting event, where children can build their own feeders to help our feathered friends during the colder months. Alongside the fun, they\'ll learn about different bird species and the importance of conservation. It\'s a creative way to connect kids with nature and teach them about the role they can play in wildlife preservation.', 'winter_bird_feeder_crafting_1.jpg');

